import React, {Component} from 'react';
import {Switch, Route, BrowserRouter as Router} from 'react-router-dom';
import './App.css';
import MyNavbar from './components/navbar';
import Default from './components/default';
import Login from './components/login';
import Signup from './components/signup';
import Logout from './components/logout'
import Home from './components/home';
import EditUser from './components/editUser';
//import "bootstrap/dist/css/bootstrap.min.css";
import addDevice from './components/addDevice';
import editDevice from './components/editDevice';
import Device from './components/device';
import { darkTheme, lightTheme } from "./globalstyles";

const themeColours = {
  light: lightTheme,
  dark: darkTheme,
 };
const ThemeContext = React.createContext();

export const ThemeProvider = ({ children }) => {
  const [themeName, setThemeName] = React.useState("dark");

  React.useEffect(() => {
    const darkOS = window.matchMedia("(prefers-color-scheme: dark)").matches;
    setTheme(darkOS ? "dark" : "light");
  }, []);

  const setTheme = (name) => {

    ///turn my configurations to css variables
    const keys = Object.keys(themeColours[name])
    keys.map((key)=>{
      const constructVar = `--${key}`
      document.body.style.setProperty(constructVar, themeColours[name][key]);
      return false /// cuz eslint just wants me to return something
    })

    setThemeName(name);

  };
}
const NavRoute = ({exact, path, component: Component}) => (
  <Route exact={exact} path={path} render={(props) => (
    <div>
      <MyNavbar />
      <Component {...props}/>
    </div>
  )}/>
)
export const useTheme = () => React.useContext(ThemeContext);
class App extends Component {
  render() {
      return (
  <ThemeContext.Provider value={{ theme: themeName, setTheme }}>
    <React.Fragment>
      <Router>
        <Switch>
        <Route exact path="/" component={Login} />
          <Route exact path="/login" component={Login} />
          <Route exact path="/logout" component={Logout} />
          <Route exact path="/signup" component={Signup} />
          <NavRoute exact path="/home" component={Home} />
          <NavRoute exact path="/device/new" component={addDevice} />
          <NavRoute exact path="/device/:handle" component={Device} />
          <NavRoute exact path="/device/:handle/edit" component={editDevice} />
          <NavRoute exact path="/user/edit" component={EditUser} />
        <NavRoute component={Default} />
        </Switch>
      </Router>
    </React.Fragment>
  </ThemeContext.Provider>
      );
  }
}
export default App;
