import React, {Component} from 'react';
import Card from 'react-bootstrap/Card'
import {Link} from 'react-router-dom';
import {CircularProgressbar} from 'react-circular-progressbar';
import 'react-circular-progressbar/dist/styles.css';
import {buildStyles} from 'react-circular-progressbar';
import {CircleSpinner} from 'react-spinners-kit';
import axiosBaseURL from '../axios.js'
;

export default class Tile extends Component {
   constructor(props) {
      super(props);
      this.state = {
         myDevice: {
            appliance_name: "My Appliance",
            device_state: 1, 
            device_battery: 100.0,
            timestamp: "2019-04-30T08:59:00.000Z",
            id: 1,
         },
         myDevices: [],
         error: false,
         loading: true
      }
   }

   componentDidMount() {
      axiosBaseURL.get("/devices")
      .then((result) => {
         this.setState({ myDevices: result.data });
         //build a list called appNames used to create the options for the <select>
         var myList = [];
         var i;
         for(i in this.state.myDevices) {
            myList.push([this.state.myDevices[i].appliance_name, this.state.myDevices[i].id, parseInt(i)+1])
         }
      })
      .catch( (error) => {
         this.setState({loading: false, error: true});
         if(error.response){
            this.setState({error_response: error.response.data});
         }
      })

      if(this.state.myDevices.device_state){
         this.setState({
            background: "success",
            statusText: "ON"
         })
      }
      else if(!this.state.myDevices.device_state){
         this.setState({background: "danger"})
      }
      this.setState({loading: false})
   }
   deleteScout = (event) => {
      //need to confirm first
      const r = window.confirm("Do you really want to delete this, it will be permanent!");
      if(r === true){
         axiosBaseURL.delete("/device/"+this.state.myDevices.device_id)
         .then((result) => {this.setState({redirect:"/"})})
         .catch((error) => {this.setState({ error: true });})
      }
   };

   render(){
      if(this.state.loading) {
         return (
            <div className="d-flex justify-content-center m-5">
               <CircleSpinner size={60} color="#686769" loading={this.state.loading} />
            </div>)
      }
      return(
         <Card bg={this.state.background} className="tile text-center col">
            <Card.Header>
               <CircularProgressbar value={this.state.device_battery} maxValue={1} text={`${this.state.device_battery}%`} styles={buildStyles({textSize: '2.2rem',textColor:'#000'})}/>
               <Card.Title className="card-title-device">{this.state.device_id}</Card.Title>
            </Card.Header>
            <Card.Body>
               <Card.Title className="card-title-appliance_name">{this.state.device_id}({this.state.appliance_name})</Card.Title>
               {/*<Card.Text className="card-text-type">{this.state.appliance_name}</Card.Text>*/}
               <Card.Text className="card-text-device_state">{this.state.device_state}</Card.Text>
               <Link className="card-button btn btn-primary text-wrap" to={"/device/"+this.state.device_id}>Edit Device</Link>
               {/*I was thinking about using a dropdown here instead so that you can delete a scout without having
               to go to the edit scout page, which may not load if the appliance of the scout doesnt exist
               <DropdownButton device_id="dropdown-button" title="Dropdown button">
                  <Dropdown.Item href={"/scout/"+this.state.device_id}>Edit</Dropdown.Item>
                  <Dropdown.Item onClick={this.deleteScout}>Delete</Dropdown.Item>
               </DropdownButton>*/}
            </Card.Body>
         </Card>
      )
   }
}
