import React, {Component} from 'react'
import {Link, Redirect} from 'react-router-dom';
import axiosBaseURL from '../axios.js'


export default class Devices extends Component {
	constructor(props) {
		super(props);
		this.state = {		
			myDevice: {
				appliance_name: "My Appliance",
				device_state: 1, 
				device_battery: 100.0,
				timestamp: "2019-04-30T08:59:00.000Z",
				id: 1,
			},
		error: false,
		}
	};
	componentDidMount() {
		const handle = this.props.match.params.handle;
		this.setState({id: handle})
		axiosBaseURL.get("/devices")
		.then((result) => {
		   	this.setState({ myDevice: result.data });
			/*if (!this.props.state) {
				this.setState({error: true});
			}*/
		})
	};
	render() {
		console.log(this.state);
		if(this.state.error) {
			if(this.state.redirect) {return <Redirect to={this.state.redirect} />}
			return(<div><h3>There was an error</h3><h3>{this.state.error_response}</h3></div>)
		 }
		return(
			<div className="col mt-3">
			<div className="card bg-light">
  				<div className="card-body">
    					<h5 className="card-title text-wrap">{this.state.myDevice[id].appliance_name}</h5>
    					<p className="card-text">State:{this.state.myDevice.device_state}</p>
    					<p className="card-text">Battery:{this.state.myDevice.device_battery}</p>
						<p className="card-text">Last Seen:{this.state.myDevice.timestamp}</p>
    					<Link to={"/device/" + this.state.myDevice.id + "/edit"} className="btn btn-primary text-wrap">Modify device details</Link>
  				</div>
			</div>
			</div>
		)
	}
}
