import React, {Component} from 'react'
import {CircleSpinner} from 'react-spinners-kit' 
import axiosBaseURL from '../axios.js'


export default class EditUser extends Component {
    constructor(props) {
        super(props);
        this.state = {
            current: {
                email: "", 
                password: "",
            },
            update: {
               updateEmail: "",
               updatePass: "",
               confirmPass: ""
            },
            flag: false,
            loading: true, 
            verifyLoad: false,
            deleteLoad: false, 
            changeLoad: false, 
            error: false, 
            redirect: null,
            remember: true
        };
    }

   componentDidMount() {
      if(this.state.detail_form === false)
         axiosBaseURL.get("/user/current")
         .then((result) => {
            this.setState({
               loading: false, 
               current: {
                  email: result.data.email,
                  password: result.data.password
               }
            });
         })
         .catch((error) => {
            this.setState({loading: false, error: true});
            if(error.response){
               this.setState({error_response: error.response.status});
               if(error.response.data === "not authorized"){ this.setState({redirect: "/"}) }
            else if (error.response.data){console.log(error.response.data)}
         }
      })
   }

   delete = (event) => {
        this.setState({deleteLoad : true});
        const r = window.confirm("Are you sure?");
        if(r === true) {
            axiosBaseURL.post('/login', {email: this.state.current.email, password: this.state.current.password})
            .then((result) => {
                axiosBaseURL.delete('/user/current')
                this.setState({deleteLoad: false, redirect: "/"})
            })
            .catch((error) => {
                this.setState({deleteLoad: false, flag:true, error_response: error.response.status});
                if(error.response.data === "not authorized"){ this.setState({redirect: "/"}) }
                else if (error.response.data){console.log(error.response.data)}
            })
        }
        else this.setState({loading : false})
   }

   handleChange = (event) => {
        this.setState({
           current: {...this.state.current,[event.target.name]: event.target.value}
        });
   };

   handlePassChange = (event) => {
      this.setState({[event.target.name]: event.target.value})
   };

   updateP = (event) => {
      if(this.state.confirmPass !== this.state.currentPass) {
         alert("Passwords do not match.");
         this.setState({changeLoad: false});
         event.preventDefault();
      }
      else {
         axiosBaseURL.post('/login', {email: this.state.current.email, password: this.state.current.password, remember: true})
         .then((result) => {
            axiosBaseURL.patch('/user/current', {password: this.state.update.updatePass})
            .then((result) => {
               this.setState({changeLoad: false})
               alert("User Information Successfully Updated!");
            })
            .catch((error) => {
               this.setState({changeLoad: false, error: true, error_response: error.response.data})
               if(error.response.data === "not authorized"){ this.setState({redirect: "/"}) }
               else if (error.response.data){console.log(error.response.data)}
            });
         })
         .catch((error) => {
            this.setState({changeLoad: false, error: true, error_response: error.response.data})
            if(error.response.data === "not authorized"){ this.setState({redirect: "/"}) }
            else if (error.response.data){console.log(error.response.data)}
         });
         event.preventDefault();
      }
   }

   updateE = (event) => {
      this.setState({changeLoad: true})
      if(this.state.update.updateEmail === undefined || this.state.update.updateEmail === this.state.current.email) {
         alert("Please enter a new Email");
         this.setState({changeLoad: false});
         event.preventDefault();
      }
      else {
         axiosBaseURL.post('/login', {email: this.state.current.email, password: this.state.current.password, remember: true})
         .then((result) => {
            axiosBaseURL.patch('/user/current', {email: this.state.update.updateEmail})
            .then((result) => {
               this.setState({changeLoad: false})
               alert("User Information Successfully Updated!");
            })
            .catch((error) => {
               this.setState({changeLoad: false, error: true, error_response: error.response.data})
               if(error.response.data === "not authorized"){ this.setState({redirect: "/"}) }
               else if (error.response.data){console.log(error.response.data)}
            });
         })
         .catch((error) => {
            this.setState({changeLoad: false, error: true, error_response: error.response.data})
            if(error.response.data === "not authorized"){ this.setState({redirect: "/"}) }
            else if (error.response.data){console.log(error.response.data)}
         });
         event.preventDefault();
      }
   };

   render() {
      if(this.state.error) {
         return(<div className="m-5"><h3>Error: Not Logged In</h3></div>)  
      }
      if(this.state.loading){
         return (
            <div className="d-flex justify-content-center m-5">
               <CircleSpinner size={60} color="#686769" loading={this.state.loading} />
            </div>)
         }
      const { flag } = this.state;
      return(
<div className="m-5 text-light">
{flag && (
    <div classNAme="text-danger">Email or password incorrect</div>
)}
    <form>  
        <div className="form-group">
            <label>Change Email</label>
            <input className="form-control text-dark" name="email" id="inputEmail" type="email" onChange={this.handleChange} value={this.state.update.updateEmail} placeholder={this.state.current.email}/>
        </div>
        <button onClick={this.updateE} className="btn btn-success btn-space">Update Email<CircleSpinner size={10} color="#3BBCE5" loading={this.state.changeLoad}/></button>
        <div className="form-group">
            <label>Change Password</label>
            <input className="form-control text-dark" name="currentPass" id="inputCurrentPass" type="password" onChange={this.handlePassChange} value={this.state.update.updatePass}/>
        </div>
        <div className="form-group">
            <label>Confirm Password</label>
            <input className="form-control text-dark" name="confirmPass" id="inputConfirmPass" type="password" onChange={this.handlePassChange} value={this.state.update.confirmPass}/>
        </div>
        <button onClick={this.updateP} className="btn btn-success btn-space">Update Password<CircleSpinner size={10} color="#3BBCE5" loading={this.state.changeLoad}/></button>
    </form>
</div>
        )
    }
}
